        kiste = jRadioButton2.isSelected();
                
        if(urne == true) {
            System.out.println("radioButton1 er valgt");
        }else if (kiste == true){
            System.out.println("radioButton2 er valgt");        
        }else{
            System.out.println("Ingen radioButton er valgt");
        }
